func TestGetRecords(t *testing.T) {
	db, mock, err := sqlmock.New()
	if err != nil {
		t.Fatalf("Error creando el mock de la base de datos: %v", err)
	}

	apiServer := NewApiServer(":8080")
	apiServer.DB = db

	req, _ := http.NewRequest("GET", "/record?quant=12", nil)

	response := httptest.NewRecorder()
	l2, _ := utils.GetParamsRequest(req)

	//mock.ExpectBegin()
	t.Run("Validar numero", func(t *testing.T) {
		// validate quant
		assert.True(t, utils.IsValidInteger(recordsexpected[0].Quant), "número entero válido")
		if !utils.IsEmpty(l2[2]) {
			assert.True(t, utils.IsValidInteger(l2[2]), "número cantidad inválido")
			//assert.False(t, utils.IsValidInteger(l2[2]), "numero no es entero")
		}
		//validate price
		if !utils.IsEmpty(l2[3]) {
			assert.True(t, utils.IsValidFloat(l2[3]), "número precio inválido")
			//assert.False(t, utils.IsValidInteger(l2[2]), "numero no es fraccionario")
		}
		//validate page
		if !utils.IsEmpty(l2[7]) {
			assert.True(t, utils.IsValidInteger(l2[7]), "número de paginacion inválido")
			//assert.False(t, utils.IsValidInteger(l2[2]), "numero no es fraccionario")
		}
		//validate limit
		if !utils.IsEmpty(l2[8]) {
			assert.True(t, utils.IsValidInteger(l2[8]), "número de limite inválido")
			//assert.False(t, utils.IsValidInteger(l2[2]), "numero no es fraccionario")
		}
	})

	t.Run("Validar fecha", func(t *testing.T) {
		// validate created
		if !utils.IsEmpty(l2[5]) {
			assert.True(t, utils.IsValidDate(l2[5], "created"), "fecha inválida")
			//assert.False(t, utils.IsValidInteger(l2[2]), "numero no es entero")
		}
		// validate published
		if !utils.IsEmpty(l2[6]) {
			assert.True(t, utils.IsValidDate(l2[6], "published"), "número fraccionario inválido")
			//assert.False(t, utils.IsValidInteger(l2[2]), "numero no es fraccionario")
		}
	})
	query := GetQuery(l2)

	handler := http.HandlerFunc(apiServer.HandlerRecord)
	t.Run("Validar Query", func(t *testing.T) {
		db.Begin()
		rows := sqlmock.NewRows([]string{"record_name", "descr", "img", "price", "topic", "created", "published", "quant"}).
			AddRow(recordsexpected[0].Name, recordsexpected[0].Descr, recordsexpected[0].Image, recordsexpected[0].Price, recordsexpected[0].Topic, recordsexpected[0].Created, recordsexpected[0].Published, recordsexpected[0].Quant).
			AddRow(recordsexpected[1].Name, recordsexpected[1].Descr, recordsexpected[1].Image, recordsexpected[1].Price, recordsexpected[1].Topic, recordsexpected[1].Created, recordsexpected[1].Published, recordsexpected[0].Quant).
			AddRow(recordsexpected[2].Name, recordsexpected[2].Descr, recordsexpected[2].Image, recordsexpected[2].Price, recordsexpected[2].Topic, recordsexpected[2].Created, recordsexpected[2].Published, recordsexpected[0].Quant).
			AddRow(recordsexpected[3].Name, recordsexpected[3].Descr, recordsexpected[3].Image, recordsexpected[3].Price, recordsexpected[3].Topic, recordsexpected[3].Created, recordsexpected[3].Published, recordsexpected[0].Quant).
			AddRow(recordsexpected[4].Name, recordsexpected[4].Descr, recordsexpected[4].Image, recordsexpected[4].Price, recordsexpected[4].Topic, recordsexpected[4].Created, recordsexpected[4].Published, recordsexpected[0].Quant).
			AddRow(recordsexpected[5].Name, recordsexpected[5].Descr, recordsexpected[5].Image, recordsexpected[5].Price, recordsexpected[5].Topic, recordsexpected[5].Created, recordsexpected[5].Published, recordsexpected[0].Quant).
			AddRow(recordsexpected[6].Name, recordsexpected[6].Descr, recordsexpected[6].Image, recordsexpected[6].Price, recordsexpected[6].Topic, recordsexpected[6].Created, recordsexpected[6].Published, recordsexpected[0].Quant).
			AddRow(recordsexpected[7].Name, recordsexpected[7].Descr, recordsexpected[7].Image, recordsexpected[7].Price, recordsexpected[7].Topic, recordsexpected[7].Created, recordsexpected[7].Published, recordsexpected[0].Quant).
			AddRow(recordsexpected[8].Name, recordsexpected[8].Descr, recordsexpected[8].Image, recordsexpected[8].Price, recordsexpected[8].Topic, recordsexpected[8].Created, recordsexpected[8].Published, recordsexpected[0].Quant).
			AddRow(recordsexpected[9].Name, recordsexpected[9].Descr, recordsexpected[9].Image, recordsexpected[9].Price, recordsexpected[9].Topic, recordsexpected[9].Created, recordsexpected[9].Published, recordsexpected[9].Quant)
		exp := mock.ExpectQuery(regexp.QuoteMeta(query)).
			WillReturnRows(rows)

		mock.ExpectCommit()
		status := response.Code

		if status != http.StatusOK {
			t.Errorf("Handler returned a wrong status code: got %v want %v", status, http.StatusOK)
		}
		handler.ServeHTTP(response, req)
		if err != nil {
			t.Errorf("err: %s", err)
		}

		assert.NotNil(t, exp)
		defer db.Close()
	})

}

func GetQuery(params []string) string {
	paths := []string{"name", "descr", "quant", "price", "topic", "created", "published", "page", "limit"}
	list := make([]interface{}, 9)
	for i := 0; i < len(paths); i++ {
		if params[i] != "" {
			if i == 2 || i == 3 || i == 7 || i == 8 {
				list[i] = params[i]
			} else {
				list[i] = "'" + params[i] + "'"
			}
		} else {
			list[i] = "NULL"
		}
	}
	return fmt.Sprintf("SELECT * FROM filter_records (%s, %s, %s, %s, %s, %s, %s, %s, %s)", list[0], list[1], list[2], list[3], list[4], list[5], list[6], list[7], list[8])
}
